parameters.code:
script({
	inputs:{
	},

	outputs:{
	     week:'array',
	     firstDate:"string",
	     firstDay: "string",
	     timeset: "signal"
	},

	signals:{
		 getWeek:function() { 
		    let baseDate = new Date();
		    let weekDays = []; 
		    let firstDayDate;
		    let firstDayName;


		    for (let i = 0; i < 7; i++){
		        let weekDay = baseDate.toLocaleDateString('en-us', {weekday: 'short'});
		        let weekDay_long = baseDate.toLocaleDateString('en-us', {weekday: 'long'});
		        let day = baseDate.getUTCDate()+1;
		        let month = baseDate.getUTCMonth()+1;
		        let dateAsString =  day + "/" + month;
		        if(i == 0){
		            firstDayDate = dateAsString; 
		            firstDayName = weekDay;
		        }

		        baseDate.setDate(baseDate.getDate()+1);

		        weekDays.push({"weekday":weekDay, "weekday_long": weekDay_long, "date":dateAsString});
		    }

		    this.setOutputs({week: weekDays, firstDate:firstDayDate, firstDay:firstDayName});
		    this.sendSignalOnOutput("timeset");

		 }
	}
})

